import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import majorsData from '../majors.json';

function SearchBar() {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Find the major in your data by name
    const major = majorsData.find(
      (m) => m.fields.name.toLowerCase() === searchTerm.toLowerCase()
    );

    if (major) {
      // Navigate directly to the detail page using pk
      navigate(`/MajorsView/${major.pk}`);
    } else {
      alert('Major not found!');
    }
  };


}

export default SearchBar;
