import React, { useEffect, useState } from 'react';
import API from '../api';
import MajorCard from '../components/MajorCard';
import SearchBar from '../components/SearchBar';
import './MajorsList.css';

function MajorsList() {
  const [majors, setMajors] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Load favorites from LocalStorage
    const favs = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(favs);

    // Fetch majors from API
    API.get('majors/')
      .then(res => setMajors(res.data))
      .catch(err => console.error(err));
  }, []);

  const toggleFavorite = (id) => {
    let updated = [...favorites];
    if (favorites.includes(id)) {
      updated = updated.filter(favId => favId !== id);
    } else {
      updated.push(id);
    }
    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  // Filter majors by search term
  const filtered = majors.filter(m =>
    m.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="majors-list-page">
      <h2>Majors</h2>
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      <div className="majors-list">
        {filtered.map(major => (
          <MajorCard
            key={major.id}
            major={major}
            isFavorite={favorites.includes(major.id)}
            toggleFavorite={() => toggleFavorite(major.id)}
          />
        ))}
      </div>
    </div>
  );
}

export default MajorsList;
