import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import majorsData from '../majors.json'; // Import your JSON file

function MajorDetail() {
  const { id } = useParams();
  const [major, setMajor] = useState(null);

  useEffect(() => {
    // Find the major with matching pk
    const foundMajor = majorsData.find((m) => m.pk === parseInt(id));
    if (foundMajor) {
      setMajor(foundMajor.fields); // store fields only
    }
  }, [id]);

  if (!major) return <p>Loading...</p>;

  return (
    <div className="major-detail">
      <h2>{major.name}</h2>
      <p>{major.about}</p>
      <p><strong>Career Prospects:</strong> {major.career_prospects}</p>
      <p><strong>Universities:</strong> {major.universities}</p>
      <p><strong>Entry Level Salary:</strong> ${major.entry_level_salary}</p>
      <p><strong>Mid Level Salary:</strong> ${major.mid_level_salary}</p>
      <p><strong>Senior Level Salary:</strong> ${major.senior_level_salary}</p>
      <p><strong>Employment Rate:</strong> {major.employment_rate}%</p>
    </div>
  );
}

export default MajorDetail;
