import React, { useEffect, useState } from 'react';
import API from '../api';
import MajorCard from '../components/MajorCard';
import './Favorite.css';

function Favorite() {
  const [majors, setMajors] = useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Load favorites from LocalStorage
    const favs = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(favs);

    // Fetch majors from API
    API.get('majors/')
      .then(res => setMajors(res.data))
      .catch(err => console.error(err));
  }, []);

  const toggleFavorite = (id) => {
    let updated = [...favorites];
    if (favorites.includes(id)) {
      updated = updated.filter(favId => favId !== id);
    } else {
      updated.push(id);
    }
    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  // Show only majors that are in favorites
  const favoriteMajors = majors.filter(m => favorites.includes(m.id));

  return (
    <div className="favorites-page">
      <h2>My Favorites</h2>
      {favoriteMajors.length === 0 ? (
        <p>No favorites added yet.</p>
      ) : (
        <div className="majors-list">
          {favoriteMajors.map(major => (
            <MajorCard
              key={major.id}
              major={major}
              isFavorite={favorites.includes(major.id)}
              toggleFavorite={() => toggleFavorite(major.id)}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default Favorite;
