import React, { useEffect, useState } from 'react';
import majorsData from '../majors.json';
import './Favorite.css';

function Favorite() {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Load favorite IDs from localStorage
    const favIds = JSON.parse(localStorage.getItem('favorites')) || [];

    // Make sure IDs are numbers (localStorage stores as strings sometimes)
    const favIdsNum = favIds.map(id => Number(id));

    // Filter majors by IDs
    const favMajors = majorsData.filter(m => favIdsNum.includes(m.pk));
    setFavorites(favMajors);
  }, []);

  const handleRemove = (pk) => {
    const updatedFavs = favorites.filter(m => m.pk !== pk);
    setFavorites(updatedFavs);

    // Update localStorage
    const favIds = updatedFavs.map(m => m.pk);
    localStorage.setItem('favorites', JSON.stringify(favIds));
  };

  return (
    <div>
      <h1>My Favorites</h1>
      {favorites.length === 0 ? (
        <p>No favorites yet.</p>
      ) : (
        <ul>
          {favorites.map(major => (
            <li key={major.pk}>
              <h2>{major.fields.name}</h2>
              <p>{major.fields.about}</p>
              <button onClick={() => handleRemove(major.pk)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Favorite;
