import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import majorsData from '../majors.json';
import './MajorsView.css';

function MajorsView() {
  const { id } = useParams(); // Get the major id from the URL
  const [major, setMajor] = useState(null);

  useEffect(() => {
    const foundMajor = majorsData.find((m) => m.pk === parseInt(id));
    if (foundMajor) {
      setMajor(foundMajor.fields);
    }
  }, [id]);

  if (!major) return <p>Loading major details...</p>;

  return (
    <div className="major-view-page">
      <h2>{major.name}</h2>
      <div className="major-card">
        <p><strong>About:</strong> {major.about}</p>
        <p><strong>Career Prospects:</strong> {major.career_prospects}</p>
        <p><strong>Universities:</strong> {major.universities}</p>
        <p><strong>Entry Level Salary:</strong> ${major.entry_level_salary}</p>
        <p><strong>Mid Level Salary:</strong> ${major.mid_level_salary}</p>
        <p><strong>Senior Level Salary:</strong> ${major.senior_level_salary}</p>
        <p><strong>Employment Rate:</strong> {major.employment_rate}%</p>
      </div>
    </div>
  );
}

export default MajorsView;
