import React, { useEffect, useState } from 'react';
import API from '../api';
import MajorCard from '../components/MajorCard';
import './Favorite.css';

function Favorite() {
  const [majors, setMajors] = useState([]);
  const [favorite, setFavorites] = useState([]);

  useEffect(() => {
    // Load favorites from LocalStorage
    const favs = JSON.parse(localStorage.getItem('favorite')) || [];
    setFavorites(favs);

    // Fetch majors from API
    API.get('majors/')
      .then(res => setMajors(res.data))
      .catch(err => console.error(err));
  }, []);

  // Only show majors that are in favorites
  const favoriteMajors = majors.filter(m => favorites.includes(m.pk));

  return (
    <div className="favorites-page">
      <h2>My Favorites</h2>
      {favoriteMajors.length === 0 ? (
        <p>No favorites yet.</p>
      ) : (
        <div className="majors-list">
          {favoriteMajors.map(major => (
            <MajorCard key={major.pk} major={major} />
          ))}
        </div>
      )}
    </div>
  );
}

export default Favorite;
