import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import majorsData from '../majors.json';
import Suggest from './Suggest';

function SearchBar() {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const navigate = useNavigate();

  const handleSubmit = (term) => {
    // Find the major by exact name (case-insensitive)
    const major = majorsData.find(
      (m) => m.fields.name.toLowerCase() === term.trim().toLowerCase()
    );
    if (major) {
      navigate(`/MajorsView/${major.pk}`);
    } else {
      alert('Major not found!');
    }
  };

  const handleChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Filter suggestions while typing
    if (value.length > 0) {
      const filtered = majorsData.filter((m) =>
        m.fields.name.toLowerCase().includes(value.toLowerCase())
      );
      setSuggestions(filtered);
    } else {
      setSuggestions([]);
    }
  };

  const handleSelect = (name) => {
    setSearchTerm(name);
    setSuggestions([]);
    handleSubmit(name); // Automatically navigate to the major
  };

  return (
    <div className="search-bar-wrapper">
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleSubmit(searchTerm);
        }}
        className="search-bar"
      >
        <input
          type="text"
          placeholder="Search for a major..."
          value={searchTerm}
          onChange={handleChange}
        />
        <button type="submit">Search</button>
      </form>
      {suggestions.length > 0 && (
        <Suggest suggestions={suggestions} onSelect={handleSelect} />
      )}
    </div>
  );
}

export default SearchBar;
