import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';
import favoriteIcon from '../assets/favorite.png';
import './MajorsView.css';

function MajorsView() {
  const { id } = useParams();
  const [major, setMajor] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    API.get(`majors/${id}/`)
      .then(res => setMajor(res.data))
      .catch(err => console.error(err));
  }, [id]);

  useEffect(() => {
    const favs = JSON.parse(localStorage.getItem('favorites')) || [];
    if (major) setIsFavorite(favs.includes(major.id));
  }, [major]);

  const toggleFavorite = () => {
    if (!major) return;
    const favs = JSON.parse(localStorage.getItem('favorites')) || [];
    let updated;
    if (favs.includes(major.id)) {
      updated = favs.filter(favId => favId !== major.id);
      setIsFavorite(false);
    } else {
      updated = [...favs, major.id];
      setIsFavorite(true);
    }
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  if (!major) return <p>Loading...</p>;

  const employmentRate = major.employment_rate;
  let circleColor = 'red';
  if (employmentRate >= 80) circleColor = 'green';
  else if (employmentRate >= 50) circleColor = 'orange';

  return (
    <div className="majors-view-container">
      <div className="info-box">
        <h2>{major.name}</h2>
        <p>{major.about}</p>
        <p><strong>Career Prospects:</strong> {major.career_prospects}</p>
        <p><strong>Universities:</strong> {major.universities}</p>
        <p><strong>Entry Level Salary:</strong> ${major.entry_level_salary}</p>
        <p><strong>Mid Level Salary:</strong> ${major.mid_level_salary}</p>
        <p><strong>Senior Level Salary:</strong> ${major.senior_level_salary}</p>

        <div className="employment-circle" style={{ borderColor: circleColor }}>
          <span>{employmentRate}%</span>
        </div>

        <button className="favorite-btn" onClick={toggleFavorite}>
          <img src={favoriteIcon} alt="Favorite" className={isFavorite ? 'active' : ''} />
        </button>
      </div>
    </div>
  );
}

export default MajorsView;
