import { useEffect, useState } from 'react';
import majorsData from '../majors.json'; // make sure path is correct

function Favorite() {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Load saved favorites from localStorage
    const favs = JSON.parse(localStorage.getItem('favorites')) || [];
    // Map saved IDs back to actual majors
    const favMajors = majorsData.filter(m => favs.includes(m.pk));
    setFavorites(favMajors);
  }, []);

  return (
    <div>
      <h1>My Favorites</h1>
      {favorites.length === 0 ? (
        <p>No favorites yet.</p>
      ) : (
        <ul>
          {favorites.map(major => (
            <li key={major.pk}>
              <h3>{major.fields.name}</h3>
              <p>{major.fields.about}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Favorite;
