import React, { useState } from 'react';
import './Home.css';

function Home() {
  const [hobby, setHobby] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  const getAISuggestions = () => {
    if (!hobby) return;
    const map = {
      coding: ['Computer Science', 'Software Engineering'],
      painting: ['Fine Arts', 'Graphic Design'],
      business: ['Business Administration', 'Economics'],
      sports: ['Sports Science', 'Physiotherapy'],
    };
    const lowerHobby = hobby.toLowerCase();
    const result = map[lowerHobby] || ['General Studies'];
    setSuggestions(result);
  };

  const handleSuggestionClick = (major) => {
    setSearchQuery(major);
  };

  return (
    <div className="home">
      {/* Welcome heading at top */}
      <h1 className="welcome-heading">Welcome to Majorly</h1>

      {/* Bottom area of top section */}
      <div className="bottom-top-section">
        {/* Left side: suggestions & hobby */}
        <div className="left-bottom">
          <div className="hobby-box">
            <input
              type="text"
              placeholder="Type your hobby..."
              value={hobby}
              onChange={(e) => setHobby(e.target.value)}
            />
            <button onClick={getAISuggestions}>Get Suggested Majors</button>
          </div>

          {suggestions.length > 0 && (
            <div className="suggestions">
              <ul>
                {suggestions.map((major, index) => (
                  <li key={index} onClick={() => handleSuggestionClick(major)}>
                    {major}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* Right side: search bar */}
        <div className="right-bottom">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search for a major..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={() => alert(`Searching for: ${searchQuery}`)}>
              Search
            </button>
          </div>
        </div>
      </div>

      {/* About Section at bottom */}
      <div className="about-app">
        <h2>About Majorly</h2>
        <p>
          Majorly helps you explore majors and careers that match your hobbies
          and interests. Type your hobby above and let our AI suggest the
          perfect major for you!
        </p>
      </div>
    </div>
  );
}

export default Home;
