import React, { useEffect, useState } from 'react';
import API from '../api';
import MajorCard from '../components/MajorCard';
import './Favorite.css';

function Favorite() {
  const [majors, setMajors] = useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Load favorites from LocalStorage
    const favs = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(favs);

    // Fetch majors from API
    API.get('majors/')
      .then(res => {
        console.log("Majors from API:", res.data);
        setMajors(res.data);
      })
      .catch(err => console.error(err));
  }, []);

  const toggleFavorite = (pk) => {
    let updated = [...favorites];
    if (favorites.includes(pk)) {
      updated = updated.filter(favPk => favPk !== pk);
    } else {
      updated.push(pk);
    }
    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  // Filter only favorite majors
  const favoriteMajors = majors.filter(m => favorites.includes(m.pk));

  return (
    <div className="favorites-page">
      <h2>My Favorites</h2>
      <div className="majors-list">
        {favoriteMajors.length > 0 ? (
          favoriteMajors.map(major => (
            <MajorCard
              key={major.pk}
              major={major}
              isFavorite={favorites.includes(major.pk)}
              toggleFavorite={() => toggleFavorite(major.pk)}
            />
          ))
        ) : (
          <p>No favorites yet.</p>
        )}
      </div>
    </div>
  );
}

export default Favorite;
