// src/favorites.js
const KEY = 'favorites';

// Load favorites
export function loadFavorites() {
  try {
    const fav = JSON.parse(localStorage.getItem(KEY)) || [];
    return fav.map(Number); // ensure they are numbers
  } catch {
    return [];
  }
}

// Save favorites
export function saveFavorites(list) {
  localStorage.setItem(KEY, JSON.stringify(list.map(Number)));
}

// Add or remove a favorite
export function toggleFavorite(pk) {
  const favorites = loadFavorites();
  let updated;
  if (favorites.includes(pk)) {
    updated = favorites.filter(id => id !== pk);
  } else {
    updated = [...favorites, pk];
  }
  saveFavorites(updated);
  return updated;
}
