import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import majorsData from '../majors.json';
import favoriteIcon from '../assets/favorite.png';
import { loadFavorites, toggleFavorite } from '../favorites';
import './MajorsView.css';

function MajorsView(props) {
  const params = useParams();
  const id = parseInt(params.id);
  const major = majorsData.find(function(m) { return m.pk === id });

  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(function() {
    const favorites = loadFavorites();
    setIsFavorite(favorites.includes(major ? major.pk : 0));
  }, [major]);

  const handleToggle = function() {
    const updated = toggleFavorite(major.pk);
    setIsFavorite(updated.includes(major.pk));
    if (props.onFavoritesChange) props.onFavoritesChange(updated);
  };

  if (!major) return React.createElement('p', null, 'Major not found!');

  return React.createElement('div', { className: 'majors-view-container' },
    React.createElement('div', { className: 'info-box' },
      React.createElement('h2', null, major.fields.name),
      React.createElement('p', null, major.fields.about),
      React.createElement('p', null, 'Career Prospects: ' + major.fields.career_prospects),
      React.createElement('p', null, 'Universities: ' + major.fields.universities),
      React.createElement('button', { className: 'favorite-btn', onClick: handleToggle },
        React.createElement('img', { src: favoriteIcon, alt: 'Favorite', className: isFavorite ? 'active' : '' }),
        isFavorite ? ' Remove from Favorites' : ' Add to Favorites'
      )
    )
  );
}

export default MajorsView;
