import React, { useState } from 'react';
import './Home.css';
import majorsData from '../'; 
// Make sure your JSON has a structure like:
// [
//   {
//     "pk": 1,
//     "fields": {
//       "name": "Computer Science",
//       "skills": ["coding", "programming", "logic"]
//     }
//   },
//   ...
// ]

function Home() {
  const [hobby, setHobby] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  const getAISuggestions = () => {
    if (!hobby) return;

    const lowerHobby = hobby.toLowerCase();

    // Filter majors that have a skill matching the hobby
    const matchedMajors = majorsData.filter(major =>
      major.fields.skills.some(skill =>
        lowerHobby.includes(skill.toLowerCase())
      )
    ).map(m => m.fields.name);

    // Fallback if no match
    setSuggestions(matchedMajors.length > 0 ? matchedMajors : ['General Studies']);
  };

  const handleSuggestionClick = (major) => {
    setSearchQuery(major);
  };

  return (
    <div className="home">
      <div className="welcome-section">
        <h1>Welcome to Majorly</h1>
      </div>

      <div className="bottom-top-section">
        <div className="left-bottom">
          <div className="hobby-box">
            <input
              type="text"
              placeholder="Type your hobby..."
              value={hobby}
              onChange={(e) => setHobby(e.target.value)}
            />
            <button onClick={getAISuggestions}>Get Suggested Majors</button>
          </div>

          {suggestions.length > 0 && (
            <div className="suggestions">
              <ul>
                {suggestions.map((major, index) => (
                  <li key={index} onClick={() => handleSuggestionClick(major)}>
                    {major}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        <div className="right-bottom">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search for a major..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={() => alert(`Searching for: ${searchQuery}`)}>
              Search
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
